import React, { Component, Fragment } from 'react'
import { Consumer } from '../context'

 class AddContact extends Component {
     state = {
         nom : "",
         tel : "",
         email : ""
     }

     onChangeInput = (e) => this.setState({ [e.target.name] : e.target.value })
     submit = (e) => {
         e.preventDefault(); 
         console.log(this.state);
     }
      render() {
        const {nom, tel, email } = this.state;
          return (
            <Consumer>

                    {
                        value => {
                            const {dispatch} = value;
                            return (
                                <Fragment>
                                    <div className="container">
                                        <form onSubmit={this.submit.bind(this, dispatch)}>
                                        <div class="card" style={{ width:'50rem', position:'center'}}>
                                            <div class="card-body">
                                                <h4 class="card-title">  Add Contact </h4>
                                                <div class="card-text">
                                                    <div className="form-group">
                                                        <label htmlFor=""> Nom : </label>
                                                        <input type="text" className="form-control"
                                                        defaultValue = {nom}
                                                        name = "nom"
                                                        onChange = {this.onChangeInput}
                                                        />
                                                    </div>
                                                    <div className="form-group">
                                                        <label htmlFor=""> Tel :</label>
                                                        <input type="text" className="form-control"
                                                        defaultValue = {tel}
                                                        name = "tel"
                                                        onChange = {this.onChangeInput}
                                                        />
                                                    </div>
                                                    <div className="form-group">
                                                        <label htmlFor="">Email :</label>
                                                        <input type="text" className="form-control"
                                                        defaultValue = {email}
                                                        name = "email"
                                                        onChange = {this.onChangeInput}
                                                        />
                                                    </div>
                                                    <button className="btn btn-success btn-block"> Add new Contact </button>
                                                </div>
                                            </div>
                                        </div>
                                        </form>
                                    </div>
                                </Fragment>
                            )
                        }
                    }

            </Consumer>
          )}
}

export default AddContact;
