import React, { Component, Fragment } from 'react'
import {Consumer} from '../context'
import './contact.css'
import PropTypes from 'prop-types'

class Contact extends Component {

    state = {
        isActiveShowContact: true
    } 

    showContact(ss) {
        console.log('salam 3alikom', ss)
        this.setState({
            isActiveShowContact: !this.state.isActiveShowContact
        })
    }
    deleteContact(){

    }
    render() {
        const {nom , tel , email } = this.props.data;
        return (
                <Consumer>
                    { value => (
                        <Fragment>
                                <div className="card">
                                    <div className="card-body">
                                        <h4 className="card-title"> 
                                        { nom} 
                                            <i onClick = { this.showContact.bind(this) } className="fa fa-sort-down dropDown-Contact"> </i> 
                                            <i onClick = { this.deleteContact.bind(this) } class="fa fa-times delete-Contact" aria-hidden="true"></i>
                                        </h4>  
                                        <hr />
                                        <div className="card-text">
                                            {(this.state.isActiveShowContact) ? (
                                            <ul className="list-group">
                                                <li className="list-group-item"> { tel }   </li>
                                                <li className="list-group-item"> { email } </li>
                                            </ul>
                                            ): null }
                                        </div>
                                    </div>
                                </div>
                        </Fragment>
                    )}
                </Consumer>


        )
    }
}

Contact.defaultProps = {
    nom : "name required",
    tel : "tel required",
    email : "email required"
}

Contact.propTypes = {
    data : PropTypes.object.isRequired
}

export default Contact;
